<html>
  <head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <style>
      *{margin:0;padding:0;text-decoration:none;color:#000;}
      h1{margin:0.5em 1em ;}
      ul{list-style:none;align:center;}
      li{float:left;margin: 4px;}
      a{transition:0.2s;display:block;width:8em;text-align:center;min-height:120px;border:2px solid;border-radius: 8px;border-color:#FFF;text-shadow:0 0 4px #999;}
      a:hover{transition: 0.2s;background:#CCD;}
      a:active{background:#DDE;}
      .header{background:#EEE;margin:0;padding:0;}
      .iconFolder {width:8em;height:5em;top:8px;background:url("data:image/png;base64,") bottom;background-size: 3.8em;background-repeat: no-repeat;margin:4px auto;}
      .iconFile {width:8em;height:5em;top:8px;background:url("data:image/png;base64,") bottom;background-size: 3.8em;background-repeat: no-repeat;margin:4px auto;}
      .fileName{margin:2px auto;width:7.5em;word-wrap:break-word;height:3.5em;overflow:hidden;}
      .fileSize{position:relative;text-align:center;right:8px;top:4px;border:0 solid;border-radius:8px;background:rgba(200,200,200,0.5);float:right;font-size:0.8em;padding:2px;}
    </style>
  </head>
  <body>
  <div class="header">
    <h1><?php echo urldecode($path);?></h1>
  </div>
  <ul>
  <?php if($path != '/'):?>
    <li>
      <a href="<?php echo get_absolute_path($root.$path.'../');?>">
        <div class="iconFolder">
          <span class="fileSize"></span>
        </div>
        <p class="fileName">..</p>
      </a>
  </li>
  <?php endif;?>
  <?php foreach((array)$items as $item):?>
  <?php if(!empty($item['folder'])):?>
  <li>
    <a href="<?php echo get_absolute_path($root.$path.$item['name']);?>">
      <div class="iconFolder">
          <span class="fileSize"><?php echo $item['size'];?></span>
      </div>
      <p class="fileName"><?php e($item['name']);?></p>
   </a>
  </li>
  <?php else:?>
  <li>
    <a href="<?php echo get_absolute_path($root.$path).urlencode($item['name']);?>" target="_blank">
      <div class="iconFile">
          <span class="fileSize"><?php echo $item['size'];?></span>
      </div>
      <p class="fileName"><?php e($item['name']);?></p>
    </a>
  </li>
  <?php endif;?>
  <?php endforeach;?>
  </ul>
  </body>
</html>
